script_folder="/home/mmom/src/tmp/magickwand.js/conan"
echo "echo Restoring environment" > "$script_folder/deactivate_conanrunenv-release-x86_64.sh"
for v in FONTCONFIG_FILE FONTCONFIG_PATH PATH OPENSSL_MODULES
do
    is_defined="true"
    value=$(printenv $v) || is_defined="" || true
    if [ -n "$value" ] || [ -n "$is_defined" ]
    then
        echo export "$v='$value'" >> "$script_folder/deactivate_conanrunenv-release-x86_64.sh"
    else
        echo unset $v >> "$script_folder/deactivate_conanrunenv-release-x86_64.sh"
    fi
done


export FONTCONFIG_FILE="/home/mmom/.conan2/p/b/fontc994b6a6673260/p/bin/etc/fonts/fonts.conf:$FONTCONFIG_FILE"
export FONTCONFIG_PATH="/home/mmom/.conan2/p/b/fontc994b6a6673260/p/bin/etc/fonts:$FONTCONFIG_PATH"
export PATH="/home/mmom/.conan2/p/lzma_34ec3433bed5e/p/bin:$PATH"
export OPENSSL_MODULES="/home/mmom/.conan2/p/b/opens0eb429220e7a1/p/lib/ossl-modules"